import NavigationBar from "./NavigationBar";
import './text.css'
import { useState, useEffect } from "react"
import moment from 'moment'
import Axios from 'axios'
import axios from "axios";

function EntryList({list, deleteEntry}) {
    // why got => e ?
    // Higher order delete entry click handler
    const handleDeleteEntry = id => {
        Axios.delete(`http://localhost:3001/delete/${id}`)
    }

    return (
        <div className='entry-list'>
            {
                list && list.map((item, i) => {
                    console.log(item.date)
                    return (
                        <div className='card mb-2 border-dark' key={i}>
                            <div className='card-body bg-dark text-white'>
                                <h4 className='card-title'>{item.date}</h4>
                                <p className='card-text>'>{item.text}</p>
                                <button className='btn btn-danger btn-sm' onClick={handleDeleteEntry(i)}>Delete</button>
                            </div>
                        </div>
                    )
                })
            }
        </div>
    )
}

// useRef.current.focus() purpose?
function Entry({addEntry}) {
    const [message, setMessage] = useState("")
    const handleOnChange = e => setMessage(e.target.value)
    const handleOnSubmit = e => {
        e.preventDefault()
        if (message && message.trim().length > 0) {
            addEntry({
                message,
                date: Date.now()
            })
            Axios.post('http://localhost:3001/create', {
                date: moment(Date.now()).format('L'),
                text: message
            }).then(() => {
                console.log("Success")
            })
        }
        setMessage("")
    }

    return (
        <form onSubmit={handleOnSubmit}>
            <div className="form-group">
                <textarea
                    className="form-control mb-2"
                    value={message}
                    onChange={handleOnChange}
                    type="text"
                />
                <button disabled={message.trim().length === 0}
                    className="btn mb-2 btn-success form-control" type="submit">
                        Submit
                </button>
            </div>
        </form>
    )
}

export default function Record() {
    const [entries, storeEntry, deleteEntry] = useRecord();
    const handleStoreEntry = (entry) => {
        storeEntry(entry)
        getRecord()
    }
    const handleDeleteEntry = (index) => deleteEntry(index)
    const [recordArray, setRecordArray] = useState([]);
    console.log(recordArray)
    const getRecord = () => {
        axios.get('http://localhost:3001/record').then((response) => {
            setRecordArray(response.data)
        })
    }
    return (
        <div className="bg">
            <NavigationBar/>
            <div className="container">
                <h1 className='title'>
                    Record
                </h1>
                <Entry addEntry={handleStoreEntry}/>
                <EntryList list={recordArray} deleteEntry={handleDeleteEntry}/>
            </div>
            <button onClick={getRecord}>test</button>
        </div>
    )
}
