import './App.css';
import { BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import { useState, useEffect, useLayoutEffect } from 'react';
import getScrollbarWidth from "./pages/getScrollbarWidth";
import About from './pages/About';
import Home from './pages/Home';
import Record from './pages/Record';

function useBodyScrollable() {
  const [bodyScrollable, setBodyScrollable] = useState(
      document.body.scrollHeight > window.innerHeight
  )
  useEffect(() => {
      const resizeObserver = new ResizeObserver(() => {
          setBodyScrollable(document.body.scrollHeight > window.innerHeight)
      })
      resizeObserver.observe(document.body)
      return () => {
          resizeObserver.unobserve(document.body)
      }
  }, [])
  return bodyScrollable
}

const scrollbarWidth = getScrollbarWidth()

function App() {
  const bodyScrollable = useBodyScrollable()
  useLayoutEffect(() => {
      if (bodyScrollable) {
          document.body.style.paddingRight = '0px'
      } else {
          document.body.style.paddingRight = `${scrollbarWidth}px`
      }
  }, [bodyScrollable])

  return (
    <div>
      <Router>
          <Routes>
            <Route exact path='/' element={<Home/>}></Route>
            <Route path='/about' element={<About/>}></Route>
            <Route path='/record' element={<Record/>}></Route>
          </Routes>
      </Router>
    </div>
  );
}

export default App;
